# COMMAND ----------

# DBTITLE 1,Use %matplotlib inline
# MAGIC %matplotlib inline

# COMMAND ----------

import numpy as np
import matplotlib.pyplot as plt

points = 500
zorder1 = 10
zorder2 = 5

x = np.linspace(0, 1, points)
y = np.sin(4 * np.pi * x) * np.exp(-5 * x)

fig, ax = plt.subplots()

ax.fill(x, y, zorder=zorder1)
ax.grid(True, zorder=zorder2)
plt.show()
plt.close() # Ensure you close it

# COMMAND ----------

# DBTITLE 1,Matplotlib figure embedded into HTML
import base64
from io import BytesIO
tmpfile = BytesIO()

fig.savefig(tmpfile, format='png') # Create a PNG byte array from Matplotlib fig

encoded = base64.b64encode(tmpfile.getvalue()).decode("UTF-8")
html = ('<p>' + '<img src=\'data:image/png;base64,{}\'>'.format(encoded) + '</p>')

displayHTML(html)

