# COMMAND ----------

# DBTITLE 1,Display IPython image where embed = False
from IPython.display import Image
img = Image(filename='/dbfs/FileStore/plots/sine-display.png', embed = False)
# img is a <IPython.core.display.Image object>

if img.data is not None:
  import base64
  from io import BytesIO
  encoded = base64.b64encode(img.data).decode("UTF-8")
  html = ('<p>' + '<img src=\'data:image/png;base64,{}\'>'.format(encoded) + '</p>')
if img.url is not None:
  html = F"""<p><img src="{img.url}"></p>"""
displayHTML(html)

